from pydantic import BaseModel, Field
from prefab_ui import FastMCP
from prefab_ui import AppResult, ToolCall, ShowToast
from prefab_ui.components import Form, Text

mcp = FastMCP("Contacts")

class Contact(BaseModel):
    name: str = Field(min_length=1)
    email: str
    phone: str = ""

@mcp.tool(ui=True)
async def create_contact(data: Contact | None = None) -> AppResult:
    if data is None:
        return AppResult(
            view=Form.from_model(
                Contact,
                on_submit=ToolCall(
                    "create_contact",
                    on_success=ShowToast("Saved!"),
                ),
            ),
        )
    save_to_db(data)
    return AppResult(view=Text(f"Created contact: {data.name}"))

async def handler(name: str, arguments: dict) -> ActionResult:
    if name == "search_users":
        query = arguments.get("q", "")
        users = [{"name": "Alice", "email": "alice@example.com"}]
        results = [u for u in users if query.lower() in u["name"].lower()]
        return ActionResult(content={
            "state": {"results": results},
            "view": {"type": "Text", "content": "Found users"},
        })
    return ActionResult(is_error=True, error_text=f"Unknown tool: {name}")


sim = Simulator(handler)
